tinymce.PluginManager.add('recipeprosimpleedit', function(ed) {
	ed.on('change', function(e) {
		if (ed.id == 'recipe-pro-editor-notes') {
			window.RecipePro.currentRecipe.ingestNotes(ed.getBody());
		} else {
			window.RecipePro.currentRecipe.ingestInstructions(ed.getBody());
		}
		
	});
});
